function marcingalinskiFormatAccountNumber($bankAccountNumber)
{
	$tab = array("" , "", "" , "" , "" , "");
	//1010204839252734584734
	// => 10 1020 4839 2527 3458 4734
	$flag = 0;
	$wynik = "";
	for( $i = 0 ; $i < 6 ; $i++ )
	{
		for($j = $flag ; $j < strlen($bankAccountNumber) ; $j++ )
		{
			$tab[$i] .= $bankAccountNumber[$j];
			if( $j == 1 || $j == 5 || $j == 9 || $j == 13 || $j == 17 )
			{
				$flag = $j+1;
				break;
			}
		}
		$wynik .= $tab[$i];
		$wynik .= " ";
	}
	return $wynik;
}
